mapper.select:
SELECT *
FROM product


        AND id = #{id}


        AND name LIKE CONCAT('%', #{name}, '%')


        AND category = #{category}


        AND description LIKE CONCAT('%', #{description}, '%')


        AND price >= #{minPrice}


        AND price <= #{maxPrice}


        AND stock >= #{minStock}


        AND stock <= #{maxStock}

SELECT * FROM product


        category = #{category}


        AND name LIKE CONCAT('%', #{name}, '%')



            AND price BETWEEN #{minPrice} AND #{maxPrice}


            AND price >= #{minPrice}


            AND price  <=  #{maxPrice}



ORDER BY updated_time DESC

mapper.update:
UPDATE product
SET stock = stock - #{quantity}
WHERE id = #{productId}
AND stock >= #{quantity}
